import React from 'react'
import ChartBar from './ChartBar'
import './Chart.css'

const Chart = props => {
    const valueArray = props.data.map(data => data.value)
    const maxValue = Math.max(valueArray)
    return (
        <div className='chart'>
            {props.data.map(item =>
                <ChartBar
                    value={item.value}
                    label={item.label}
                    key={item.label}
                    maxValue={maxValue}
                />
            )
            }
        </div>
    )
}

export default Chart
